package Loops;

public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<=5;i++)
		{
			System.out.println(i);
		}
	System.out.println("**********");
	
	for(int j=5;j>=1;j--)
	{
	
		System.out.println(j);
	
		


}
	System.out.println("**********");
	
	
	
	
	}
}

