package Loops;

public class loop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	
		   
		        int m = 1;
		        while (m <= 5) {
		            System.out.println(m);
		            m++;
		        }
		        System.out.println("Loop finished!");
		    
	}
	

}
